import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Validation from './LoginValidation';

function Login() { // Changed the component name to start with an uppercase letter

    const [values, SetValues] = useState({
        email: '',
        password: ''
    })
    const [errors, setErrors] = useState([])

    const handleInput = (event) => {
        SetValues(prev =>({...prev, [event.target.name]: [event.target.value]}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(Validation(values));
    }
    return (
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <h2>Sign-In</h2>
                <form action='' onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <label htmlFor='email'><strong>Email</strong></label> {/* Corrected spelling of 'Email' */}
                        <input type='email' placeholder='Enter Email' 
                        onChange={handleInput}  name='email' className='form-control rounded-0' />
                        <span>[errors]</span>
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password'><strong>Password</strong></label>
                        <input type='password' placeholder='Enter Password' onChange={handleInput} name='password' className='form-control rounded-0' />
                    </div>
                    <button className='btn btn-success w-100 rounded-0'>Login</button>
                    <p>You agree to our terms and conditions.</p> {/* Corrected the text */}
                    <Link to="/signup" className='btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>Create Account</Link>
                </form>
            </div>
        </div>
    );
}

export default Login;
